import "./Level.css";
import React from 'react';
import {useState} from 'react';

const initialStae = {
        class1: "answer",
        class2: "answer",
        class3: "answer",
        class4: "answer",
}

const Level = (props) => {
const [classButton, setClassButton] = useState(initialStae);
const [correctAnswer, setCorrectAnswer] = useState("true")

    return (
        <div>
            <section className="question">
                <h1>To unlock the door you must get code which is there in the sequence. </h1> 
                <h3>Do you have any idea which comes next in this sequence?</h3>
                <h3>4, 9, 16, 25, ?</h3>
            </section>
            <div >
                <button 
                    className ="buttons"
                    onClick={() =>{
                        let newClass = {...initialStae};
                        if(classButton.class1 === "answer"){
                            newClass.class1 = "right-answer"}

                        else if(classButton.class1 === "right-answer"){
                            newClass.class1 = "answer"}

                    setClassButton(newClass)

                }}
                >36</button>
                <button
                className ="buttons"
                onClick={() =>{
                    let newClass = {...initialStae};
                    if(classButton.class2 === "answer"){
                        newClass.class2 = "else"}

                    else if(classButton.class2 === "else"){
                        newClass.class2 = "answer"}

                setClassButton(newClass)

            }}
                >30</button>
                <button
                    className ="buttons"
                    onClick={() =>{
                        let newClass = {...initialStae};
                        if(classButton.class3 === "answer"){
                            newClass.class3 = "else"}

                        else if(classButton.class3 === "else"){
                            newClass.class3 = "answer"}

                        setClassButton(newClass)

            }}
                >18</button>
                <button
                className ="buttons"
                onClick={() =>{
                    let newClass = {...initialStae};
                    if(classButton.class4 === "answer"){
                        newClass.class4 = "else"}

                    else if(classButton.class4 === "else"){
                        newClass.class4 = "answer"}

                    setClassButton(newClass)

        }}
                >2</button>
            </div>

            <div className= {classButton.class1}>
                <p >You have unlocked the door</p>
                <button>Go to the Next Level</button>
            </div>
            <div className= {classButton.class2}>
                <p>You are thinking in the right direction but the answer is not right</p>
            </div><div className= {classButton.class3}>
                <p>Think of a bigger number and remember you have limited number of choices</p>
            </div>
            <div className= {classButton.class4}>
                <p>This is nothing less than a surprise. How can you even think that the answer can be 2. I am looking to lock you on this plannet for next 2000 years. //Alien is Super Angry</p>
            </div>
        </div>
    );

}

export default Level;
